export function timeToString(time: number) {
	if (time == null) {
		return '-'
	}

	var negative
	if (time < 0) {
		negative = true
		time = -time
	}
	time = (time / 1000) | 0
	var sec = time % 60
	time = (time / 60) | 0
	var min = time % 60
	time = (time / 60) | 0
	var hour = time

  var result = ''
  if (negative) {
    result += '-'
  }

  if (hour) {
    result += hour
			+ ':' + min.toString().padStart(2, '0')
			+ ':' + sec.toString().padStart(2, '0')
  } else if (min) {
    result += min
			+ ':' + sec.toString().padStart(2, '0')
  } else {
    result += sec
  }

	return result
}

export function dateTimeToString(date: number|Date) {
	if (!date) {
		return '-- -- ---- --:--:-- --'
	}

	if (typeof date === 'number') {
		date = new Date(date)
	}

	return [
		date.getFullYear(), '-',
		((date.getMonth() + 1) + '').padStart(2, '0'), '-',
		(date.getDate() + '').padStart(2, '0'),
		' ',
		(date.getHours() + '').padStart(2, '0'),
		':', (date.getMinutes() + '').padStart(2, '0'),
		// ':', (date.getSeconds() + '').padStart(2, '0'),
	].join('')
}

export function delay(timeMilliseconds: number) {
	return new Promise(function(resolve) {
		setTimeout(resolve, timeMilliseconds)
	})
}

export const sounds = {
	beep: new Audio("data:audio/wav;base64,"),
}

export const beep = (function() {
	if (!window.AudioContext) {
		return function() {
			sounds.beep.currentTime = 200
			sounds.beep.play()
		}
	}

	// from: https://stackoverflow.com/a/49077414/5221762
	let audioContext: AudioContext = null
	return function beep(gain: number, frequency: number, duration: number){
		if (!audioContext) {
			audioContext = new window.AudioContext()
		}

		console.log("Gain:" + gain, "Hz:" + frequency, "ms:" + duration)
		const oscillatorNode = audioContext.createOscillator()
		const gainNode = audioContext.createGain()
		oscillatorNode.connect(gainNode)
		oscillatorNode.frequency.value = frequency
		oscillatorNode.type = "square"
		gainNode.connect(audioContext.destination)
		gainNode.gain.value = gain * 0.01
		oscillatorNode.start(audioContext.currentTime)
		oscillatorNode.stop(audioContext.currentTime + duration *0.001)
	}
})()
